import { Component, OnInit } from '@angular/core';
import { interval } from 'rxjs';
import { takeWhile } from 'rxjs/operators';

@Component({
  selector: 'app-token',
  templateUrl: './token.component.html',
  styleUrls: ['./token.component.css'],
})
export class TokenComponent {
  tokenNumber!: string;
  tokenExpiry!: string;
  counter: number = 100; // Número inicial de segundos
  isCounting: boolean = true; // Variable para controlar la visibilidad

  constructor() { }

  ngOnInit(): void {
    // Crea un observable que emite un valor cada segundo
    const source = interval(1000);

    // Usa takeWhile para detener el contador cuando llegue a cero
    source.pipe(
      takeWhile(() => this.counter > 0)
    ).subscribe(() => {
      this.counter--;
      if (this.counter === 100) {
        this.isCounting = false; // Oculta el contador cuando llegue a 100
      }
    });
  }
  generateToken() {
    // Lógica para generar un nuevo token y establecerlo en las propiedades
    this.tokenNumber = '123456'; // Número de token simulado
    this.tokenExpiry = '2023-09-14 12:00 PM'; // Hora de expiración simulada
  }

  generateRandomToken() {
    // Genera un número aleatorio entre 100000 y 999999 (ambos inclusive)
    min:number = 100000;
  max:number = 999999;
    const randomNumber = Math.floor(Math.random() * (max - min + 1)) + min;

    // Convierte el número aleatorio en una cadena de 6 dígitos (agregando ceros a la izquierda si es necesario)
    const token = randomNumber.toString().padStart(6, '0');

    return token;
  }

  const randomToken = generateRandomToken();
  console.log(randomToken); 

  // Método para reiniciar el token
  resetToken() {
    this.tokenNumber = '';
    this.tokenExpiry = '';
  }
}
<!-- token-virtual.component.html -->

<div class="token-container">
  <h2>Token Virtual</h2>
  <button (click)="generateToken()">Generar Token</button>
  <button (click)="resetToken()">Reiniciar Token</button>

  <div *ngIf="tokenNumber && tokenExpiry">
    <p>Número de Token: {{ tokenNumber }}</p>
    <p>Expira: {{ tokenExpiry }}</p>

    <mat-card class="example-card">
      <mat-card-header>
        <mat-card-title>Token</mat-card-title>
        <mat-card-subtitle>{{ tokenNumber }}</mat-card-subtitle>
      </mat-card-header>
      <mat-card-content>
        <div class="countdown-container">

          <div *ngIf="isCounting" class="countdown">
            <h2>{{ counter }}</h2>
          </div>
          <div *ngIf="!isCounting" class="message">
            <p>¡Tiempo terminado!</p>
          </div>
        </div>
        <section class="example-section">
          <mat-progress-bar class="example-margin" [value]="counter">
          </mat-progress-bar>
        </section>
      </mat-card-content>
      <!-- <mat-card-actions>
        <button mat-button>LIKE</button>
        <button mat-button>SHARE</button>
      </mat-card-actions> -->
    </mat-card>
  </div>
</div>
