import { Component } from '@angular/core';

@Component({
  selector: 'app-token',
  templateUrl: './token.component.html',
  styleUrls: ['./token.component.css'],
})
export class TokenComponent {
  tokenNumber!: string;
  tokenExpiry!: string ;

  generateToken() {
    // Lógica para generar un nuevo token y establecerlo en las propiedades
    this.tokenNumber = '123456'; // Número de token simulado
    this.tokenExpiry = '2023-09-14 12:00 PM'; // Hora de expiración simulada
  }

  // Método para reiniciar el token
  resetToken() {
    this.tokenNumber = '';
    this.tokenExpiry = '';
  }
}
